import React, { useEffect, useState, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import { getHouses } from '../api/house';
import { Link } from 'react-router-dom';
import AddHouseForm from '../components/AddHouseForm';
import AddRoomForm from '../components/AddRoomForm'; // Import the new component

const DashboardPage = () => {
  const { user, logout } = useAuth();
  const [houses, setHouses] = useState([]);
  const [error, setError] = useState('');

  const fetchHouses = useCallback(async () => {
    try {
      const data = await getHouses();
      setHouses(data);
    } catch (err) {
      setError('Failed to fetch houses.');
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchHouses();
  }, [fetchHouses]);

  const handleHouseAdded = (newHouse) => {
    // Refresh the list of houses after a new one is added
    fetchHouses();
  };

  const handleRoomAdded = (newRoom) => {
    // Refresh the list of houses after a new room is added, as rooms are part of houses
    fetchHouses();
  };

  return (
    <div>
      <h2>Welcome, {user ? user.name : 'Guest'}!</h2>
      <button onClick={logout}>Logout</button>

      <h3>Your Houses</h3>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {houses.length > 0 ? (
        <ul>
          {houses.map((house) => (
            <li key={house._id}>{house.name}</li>
          ))}
        </ul>
      ) : (
        <p>No houses found. Add a new house to get started!</p>
      )}

      <AddHouseForm onHouseAdded={handleHouseAdded} />
      <AddRoomForm onRoomAdded={handleRoomAdded} /> {/* Render the AddRoomForm */}

      <nav>
        <ul>
          <li><Link to="/automations">Automations</Link></li>
          <li><Link to="/lighting">Lighting Control</Link></li>
          {/* Add more navigation links as needed */}
        </ul>
      </nav>
    </div>
  );
};

export default DashboardPage;
